'use strict';

var Day = require('es-abstract/2018/Day');
var DayFromYear = require('es-abstract/2018/DayFromYear');
var YearFromTime = require('es-abstract/2018/YearFromTime');

// https://ecma-international.org/ecma-262/5.1/#sec-15.9.1.4

module.exports = function DayWithinYear(t) {
	return Day(t) - DayFromYear(YearFromTime(t));
};
