var implementation = require('string.prototype.trimleft/implementation');
var getPolyfill = require('string.prototype.trimleft/polyfill');
var shim = require('string.prototype.trimleft/shim');

var bound = callBind(getPolyfill());

define(bound, {
	getPolyfill: getPolyfill,
	implementation: implementation,
	shim: shim
});

module.exports = bound;
